import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('');

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('section');
      const scrollPosition = window.pageYOffset;

      sections.forEach((section) => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.clientHeight;
        if (scrollPosition >= sectionTop - sectionHeight / 3) {
          setActiveSection(section.getAttribute('id'));
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <Link to="/" className="logo">
        <img src="/assets/images/john-doe.png" alt="John Doe" />
      </Link>
      <input className="menu-btn" type="checkbox" id="menu-btn" checked={isMenuOpen} onChange={toggleMenu} />
      <label className="menu-icon" htmlFor="menu-btn">
        <span className="navicon"></span>
      </label>
      <ul className={`menu ${isMenuOpen ? 'responsive' : ''}`}>
        <li>
          <Link to="/" className={activeSection === 'home' ? 'active' : ''} onClick={closeMenu}>
            Home
          </Link>
        </li>
        <li>
          <Link to="/experience" className={activeSection === 'experience' ? 'active' : ''} onClick={closeMenu}>
            Experience
          </Link>
        </li>
        <li>
          <Link to="/projects" className={activeSection === 'projects' ? 'active' : ''} onClick={closeMenu}>
            Projects
          </Link>
        </li>
        <li>
          <Link to="/research" className={activeSection === 'research' ? 'active' : ''} onClick={closeMenu}>
            Research
          </Link>
        </li>
        <li>
          <Link to="/education" className={activeSection === 'education' ? 'active' : ''} onClick={closeMenu}>
            Education
          </Link>
        </li>
      </ul>
    </header>
  );
};

export default Header;

