awesome-portfolio-websites/event.html.content:
import React, { useEffect, useState } from 'react';
import '../styles/event.css';

const Events = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch('https://api.github.com/repos/smaranjitghose/awesome-portfolio-websites/contents/assets/data/events.json');
      const data = await response.json();
      const content = atob(data.content);
      const parsedEvents = JSON.parse(content);
      setEvents(parsedEvents);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  return (
    <div className="main" id="events">
      <div className="event-container">
        <div className="event-vector">
          <img src="assets/images/event/event.svg" alt="event" />
        </div>
        <div className="event-heading event-center">
          <h1>Events</h1>
        </div>
        <div className="events">
          {events.map((event, index) => (
            <div className="event-card" key={index}>
              <div className="event-image">
                <img src={event.image} alt={event.title} />
              </div>
              <div className="event-content">
                <h2>{event.title}</h2>
                <p>{event.description}</p>
                <a href={event.link} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Events;

