import React, { useState, useEffect } from 'react';
import '../styles/project.css';

const Projects = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await fetch('https://api.github.com/users/smaranjitghose/repos');
      const data = await response.json();
      const filteredProjects = data.filter(project => !project.fork && project.stargazers_count > 0)
                                   .sort((a, b) => b.stargazers_count - a.stargazers_count)
                                   .slice(0, 6);
      setProjects(filteredProjects);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  return (
    <div className="main" id="projects">
      <h1 className="project-heading">My <span>Projects</span></h1>
      <div className="project-container">
        {projects.map((project, index) => (
          <div key={index} className="project-card">
            <div className="project-content">
              <h2 className="project-title">{project.name}</h2>
              <div className="pro-details">
                <p className="pro-description">{project.description || 'No description available'}</p>
                <div className="pro-stats">
                  <div className="stats">
                    <i className="fab fa-github"></i>
                    <span className="stats-name">Stars</span>
                    <span className="stats-value">{project.stargazers_count}</span>
                  </div>
                  <div className="stats">
                    <i className="fas fa-code-branch"></i>
                    <span className="stats-name">Forks</span>
                    <span className="stats-value">{project.forks_count}</span>
                  </div>
                </div>
                <div className="pro-links">
                  <a href={project.html_url} target="_blank" rel="noopener noreferrer" className="btn btn-sm btn-outline-primary">
                    <i className="fab fa-github"></i> GitHub
                  </a>
                  {project.homepage && (
                    <a href={project.homepage} target="_blank" rel="noopener noreferrer" className="btn btn-sm btn-outline-info">
                      <i className="fas fa-link"></i> Live
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;

