  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  cursor: pointer;
}

.project-box:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.project-title {
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: var(--text-color);
}

.project-details {
  font-size: 1rem;
  color: var(--text-color);
  text-align: justify;
}

.project-tech-stack {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 1rem;
}

.project-tech-stack > * {
  margin: 0.3rem;
  padding: 0.2rem 0.5rem;
  border-radius: 0.5rem;
  font-size: 0.8rem;
  background-color: var(--button-color);
  color: var(--text-color);
}

.project-img-container {
  width: 100%;
  height: 200px;
  overflow: hidden;
  border-radius: 0.5rem;
  margin-bottom: 1rem;
}

.project-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.project-box:hover .project-img {
  transform: scale(1.05);
}

.project-links {
  display: flex;
  justify-content: space-around;
  width: 100%;
  margin-top: 1rem;
}

.project-links a {
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  text-decoration: none;
  font-weight: 600;
  transition: all 0.3s ease;
}

.project-links a:hover {
  transform: translateY(-2px);
}

.project-card-title {
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 2rem;
  color: var(--text-color);
}

/* Responsive Design */
@media (max-width: 768px) {
  .project-box {
    width: 100%;
  }
}

awesome-portfolio-websites/assets/css/te.content:
/* Techstack Section */

.tech-stack-section {
