awesome-portfolio-websites/assets/css/tr.content:
/* Travel page styles */

/* Styling for the travel section */
.travel-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  background-color: var(--bg-color);
  color: var(--text-color);
}

/* Styling for the travel header */
.travel-header {
  text-align: center;
  margin-bottom: 30px;
}

.travel-header h1 {
  font-size: 3rem;
  margin-bottom: 10px;
}

.travel-header p {
  font-size: 1.2rem;
  color: var(--text-color-secondary);
}

/* Styling for the travel grid */
.travel-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  width: 100%;
  max-width: 1200px;
}

/* Styling for individual travel cards */
.travel-card {
  background-color: var(--bg-color-secondary);
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;
}

.travel-card:hover {
  transform: translateY(-5px);
}

.travel-card img {
  width: 100%;
  height: 200px;
  object-fit: cover;
}

.travel-card-content {
  padding: 20px;
}

.travel-card-content h3 {
  font-size: 1.5rem;
  margin-bottom: 10px;
}

.travel-card-content p {
  font-size: 1rem;
  color: var(--text-color-secondary);
  margin-bottom: 15px;
}

.travel-card-content .date {
  font-size: 0.9rem;
  color: var(--text-color-tertiary);
}

/* Responsive adjustments */
@media screen and (max-width: 768px) {
  .travel-header h1 {
    font-size: 2.5rem;
  }

  .travel-header p {
    font-size: 1rem;
  }

  .travel-grid {
    grid-template-columns: 1fr;
  }
}

awesome-portfolio-websites/assets/css/ho.content:
/* Home page styling */

/* Styling for the main container */
.main {
  width: 100%;
  height: 100vh;
}

/* Styling for the home container */
.home {
  position: relative;
  height: 100%;
  width: 100%;
  overflow: hidden;
}

/* Styling for the home content */
.home .home-content {
  height: 100%;
  width: 100%;
  color: var(--text-color);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: var(--bg-color);
}

/* Styling for the text container */
.text {
  margin: 5rem 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

/* Styling for the main heading */
.text h1 {
  font-size: 4rem;
  font-weight: 600;
  margin-bottom: 0.7rem;
  font-family: "Poppins", sans-serif;
}

/* Styling for the subheading */
.text h3 {
  position: relative;
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1rem;
  font-family: "Poppins", sans-serif;
}

/* Styling for the typed text */
.text h3 span {
  color: var(--text-color);
}

/* Styling for the paragraph */
.text p {
  font-size: 1.5rem;
  font-weight: 500;
  font-family: "Poppins", sans-serif;
}

/* Styling for the social media links */
.social-icons {
  padding: 1rem;
  display: flex;
  position: relative;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin-top: 2rem;
}

/* Styling for individual social media icons */
.social-icons a {
  position: relative;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  height: 4rem;
  width: 4rem;
  margin: 0 1rem 1rem 0;
  border-radius: 50%;
  background-color: var(--text-color);
}

/* Styling for social media icon images */
.social-icons a img {
  width: 2.5rem;
}

/* Hover effect for social media icons */
.social-icons a:hover {
  background-color: var(--hover-color);
  transition: 0.5s;
}

/* Responsive design for smaller screens */
@media (max-width: 692px) {
  .text h1 {
    font-size: 5rem;
  }
  .text h3 {
    font-size: 1.5rem;
  }
  .text p {
    font-size: 1.2rem;
  }
}

/* Responsive design for even smaller screens */
@media (max-width: 371px) {
  .text h1 {
    font-size: 3rem;
  }
  .text h3 {
    font-size: 1rem;
  }
  .text p {
    font-size: 1rem;
  }
}

/* Styling for the home button */
.home .btn {
  background-color: var(--text-color);
  color: var(--bg-color);
  font-size: 1rem;
  padding: 0.8rem 2rem;
  text-decoration: none;
  border-radius: 5px;
  transition: 0.3s;
  font-family: "Poppins", sans-serif;
}

/* Hover effect for the home button */
.home .btn:hover {
  background-color: var(--hover-color);
}

/* Styling for the scroll indicator */
.scroll-indicator {
  position: absolute;
  right: -30px;
  bottom: 20px;
  width: 200px;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  transform: rotate(90deg);
}

/* Styling for the scroll line */
.scroll-indicator span {
  font-size: 1.2rem;
  color: var(--text-color);
  font-family: "Poppins", sans-serif;
}

/* Styling for the scroll line */
.scroll-line {
  width: 100px;
  height: 2px;
  border-radius: 2px;
  background-color: var(--text-color);
  margin-left: 10px;
  animation: scroll 1.5s ease-in-out infinite;
}

/* Keyframe animation for the scroll line */
@keyframes scroll {
  0% {
    width: 0;
  }
  50% {
    width: 100px;
  }
  100% {
    width: 0;
  }
}

awesome-portfolio-websites/assets/css/st.content:
/* CSS styles for the main index page */

/* Import Google Fonts */
@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600;700;800;900&display=swap");

/* Base styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Poppins", sans-serif;
}

/* Set up CSS variables for easy theming */
:root {
  --primary-color: #823ff1;
  --secondary-color: #000000;
  --text-color: #ffffff;
  --bg-color: #000000;
}

/* Dark mode styles */
[data-theme="dark"] {
  --primary-color: #823ff1;
  --secondary-color: #ffffff;
  --text-color: #ffffff;
  --bg-color: #000000;
}

/* Light mode styles */
[data-theme="light"] {
  --primary-color: #823ff1;
  --secondary-color: #000000;
  --text-color: #000000;
  --bg-color: #ffffff;
}

body {
  color: var(--text-color);
  background: var(--bg-color);
}

/* Scrollbar styles */
::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: var(--bg-color);
}

::-webkit-scrollbar-thumb {
  background: var(--primary-color);
}

::-webkit-scrollbar-thumb:hover {
  background: var(--secondary-color);
}

/* Header styles */
.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  padding: 20px 10%;
  background: transparent;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 100;
}

.logo {
  font-size: 25px;
  color: var(--text-color);
  text-decoration: none;
  font-weight: 600;
  cursor: default;
  opacity: 0;
  animation: slideRight 1s ease forwards;
}

.navbar a {
  display: inline-block;
  font-size: 18px;
  color: var(--text-color);
  text-decoration: none;
  font-weight: 500;
  margin-left: 35px;
  transition: 0.3s;
  opacity: 0;
  animation: slideTop 0.5s ease forwards;
  animation-delay: calc(0.2s * var(--i));
}

.navbar a:hover,
.navbar a.active {
  color: var(--primary-color);
}

/* Home section styles */
.home {
  position: relative;
  width: 100%;
  justify-content: space-between;
  height: 100vh;
  background: url("/assets/images/home-bg.jpg") no-repeat;
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
  padding: 70px 10% 0;
}

.home-content {
  max-width: 600px;
}

.home-content h3 {
  font-size: 32px;
  font-weight: 700;
  opacity: 0;
  animation: slideBottom 1s ease forwards;
  animation-delay: 0.7s;
}

.home-content h3:nth-of-type(2) {
  margin-bottom: 30px;
  animation: slideTop 1s ease forwards;
  animation-delay: 0.7s;
}

.home-content h3 span {
  color: var(--primary-color);
}

.home-content h1 {
  font-size: 56px;
  font-weight: 700;
  margin: -3px 0;
  opacity: 0;
  animation: slideRight 1s ease forwards;
  animation-delay: 1s;
}

.home-content p {
  font-size: 20px;
  opacity: 0;
  animation: slideLeft 1s ease forwards;
  animation-delay: 1s;
}

.home-img img {
  max-width: 450px;
  margin-right: -20px;
  opacity: 0;
  animation: zoomIn 1s ease forwards, floatImage 4s ease-in-out infinite;
  animation-delay: 2s, 3s;
}

.social-media a {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  width: 40px;
  height: 40px;
  background: transparent;
  border: 2px solid var(--primary-color);
  border-radius: 50%;
  font-size: 20px;
  color: var(--primary-color);
  text-decoration: none;
  margin: 30px 15px 30px 0;
  transition: 0.5s ease;
  opacity: 0;
  animation: slideLeft 1s ease forwards;
  animation-delay: calc(0.2s * var(--i));
}

.social-media a:hover {
  background: var(--primary-color);
  color: var(--secondary-color);
  box-shadow: 0 0 20px var(--primary-color);
}

.btn {
  display: inline-block;
  padding: 12px 28px;
  background: var(--primary-color);
  border-radius: 40px;
  box-shadow: 0 0 10px var(--primary-color);
  font-size: 16px;
  color: var(--secondary-color);
  letter-spacing: 1px;
  text-decoration: none;
  font-weight: 600;
  opacity: 0;
  animation: slideTop 1s ease forwards;
  animation-delay: 2s;
}

/* Keyframe animations */
@keyframes slideRight {
  0% {
    transform: translateX(-100px);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slideLeft {
  0% {
    transform: translateX(100px);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slideTop {
  0% {
    transform: translateY(100px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes slideBottom {
  0% {
    transform: translateY(-100px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes zoomIn {
  0% {
    transform: scale(0);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes floatImage {
  0% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-24px);
  }
  100% {
    transform: translateY(0);
  }
}

/* Media queries for responsiveness */
@media (max-width: 1200px) {
  html {
    font-size: 55%;
  }
}

@media (max-width: 991px) {
  .header {
    padding: 2rem 3%;
  }

  section {
    padding: 10rem 3% 2rem;
  }

  .home {
    padding: 0 3%;
  }

  .footer {
    padding: 2rem 3%;
  }
}

@media (max-width: 768px) {
  #menu-icon {
    display: block;
  }

  .navbar {
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    padding: 1rem 3%;
    background: var(--bg-color);
    border-top: 0.1rem solid rgba(0, 0, 0, 0.2);
    box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.2);
    display: none;
  }

  .navbar.active {
    display: block;
  }

  .navbar a {
    display: block;
    font-size: 2rem;
    margin: 3rem 0;
  }

  .home {
    flex-direction: column;
  }

  .home-content h3 {
    font-size: 2.6rem;
  }

  .home-content h1 {
    font-size: 5rem;
  }

  .home-img img {
    width: 70vw;
    margin-top: 4rem;
  }
}

@media (max-width: 450px) {
  html {
    font-size: 50%;
  }
}
